frappe.ui.form.on('Work Order', {
    production_item: function (frm) {
        // Fetch the item group for the selected production_item and set custom_manufacture_uom
        if (frm.doc.production_item) {
            frappe.db.get_value('Item', frm.doc.production_item, 'item_group', (r) => {
                if (r && r.item_group) {
                    // Check if the item group is "FG"
                    if (r.item_group === 'FG') {
                        frm.set_value('custom_manufacture_uom', 'CT'); // Set to "CT" for FG
                    } else {
                        // Set to stock_uom for other item groups
                        frappe.db.get_value('Item', frm.doc.production_item, 'stock_uom', (uom) => {
                            if (uom && uom.stock_uom) {
                                frm.set_value('custom_manufacture_uom', uom.stock_uom);
                            }
                        });
                    }
                }
            });
        }
    },
    custom_qty_manufacture_: function (frm) {
        if (frm.doc.custom_qty_manufacture_ && frm.doc.custom_manufacture_uom && frm.doc.production_item) {
            frappe.call({
                method: 'erpmco.overrides.work_order.get_converted_qty',
                args: {
                    item_code: frm.doc.production_item,
                    uom: frm.doc.custom_manufacture_uom,
                    qty: frm.doc.custom_qty_manufacture_
                },
                callback: function (r) {
                    if (r.message) {
                        frm.set_value('qty', r.message); // Set the converted qty
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Stock Entry', {
	refresh(frm) {
		// your code here
	}
});
frappe.ui.form.on('Stock Entry Detail', {
    item_code: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.item_code) {
            frappe.db.get_doc('Item', row.item_code)
                .then(item_doc => {
                    row.custom_weight_per_unit = item_doc.weight_per_unit;
                    row.custom_weight_uom = item_doc.custom_weight_uom;
                });
        }
    }
});

frappe.ui.form.on('Work Order', {
	custom_routing_name: function (frm) {
        if (frm.doc.custom_routing_name) {
            frappe.call({
                method: "erpmco.overrides.work_order.fetch_operations",
                args: {
                    routing_name: frm.doc.custom_routing_name
                },
                callback: function (r) {
                    if (r.message) {
                        frm.clear_table("operations");
                        r.message.forEach((row) => {
                            let child = frm.add_child("operations");
                            child.operation = row.operation;
                            child.workstation = row.workstation;
                            child.time_in_mins = row.time_in_mins * frm.doc.qty;
                            child.bom = frm.doc.bom_no;
                        });
                        frm.refresh_field("operations");
                    }
                }
            });
        }
    }
})

frappe.ui.form.on('Delivery Note', {
    refresh(frm) {
        // Remove the existing "Sales Order" button
        frm.remove_custom_button(__('Sales Order'), __('Get Items From'));

        // Add the new "Fetch Reserved Batches" button
        frm.add_custom_button(
            __('Sales Order'),
            function () {
                frappe.call({
                    method: 'erpmco.overrides.delivery_note.fetch_reserved_batches',
                    args: { customer: frm.doc.customer },
                    callback: function (response) {
                        if (response.message && response.message.length > 0) {
                            const data = response.message;

                            // Create the dialog
                            const dialog = new frappe.ui.Dialog({
                                title: __('Select Reserved Batches'),
                                fields: [
                                    {
                                        fieldname: 'batch_selection',
                                        fieldtype: 'Table',
                                        label: 'Reserved Batches',
                                        cannot_add_rows: true,
                                        in_place_edit: true,
                                        reqd: 1,
                                        data: [], // Initialize as empty
                                        fields: [
                                            {
                                                fieldtype: 'Data',
                                                fieldname: 'batch_no',
                                                label: 'Batch No',
                                                read_only: 1,
                                            },
                                            {
                                                fieldtype: 'Float',
                                                fieldname: 'qty',
                                                label: 'Quantity',
                                                read_only: 1,
                                            },
                                            {
                                                fieldtype: 'Data',
                                                fieldname: 'warehouse',
                                                label: 'Warehouse',
                                                read_only: 1,
                                            },
                                            {
                                                fieldtype: 'Data',
                                                fieldname: 'item_code',
                                                label: 'Item Code',
                                                read_only: 1,
                                            },
                                            {
                                                fieldtype: 'Data',
                                                fieldname: 'sales_order',
                                                label: 'Sales Order',
                                                read_only: 1,
                                            },
                                            {
                                                fieldtype: 'Data',
                                                fieldname: 'customer',
                                                label: 'Customer',
                                                read_only: 1,
                                            },
                                        ],
                                    },
                                ],
                                primary_action_label: __('Create Delivery Note'),
                                primary_action: function () {
                                    const selected_batches = dialog.get_values().batch_selection;
                                    if (selected_batches) {
                                        frappe.call({
                                            method: 'your_app.module_name.make_delivery_note_with_batches',
                                            args: { batch_data: selected_batches },
                                            callback: function (r) {
                                                if (!r.exc) {
                                                    frappe.msgprint(__('Delivery Note created successfully!'));
                                                    dialog.hide();
                                                    frm.reload_doc();
                                                }
                                            },
                                        });
                                    }
                                },
                            });

                            // Populate table with data
                            const table_field = dialog.fields_dict.batch_selection.df.data;
                            data.forEach((row) => {
                                table_field.push({
                                    batch_no: row.batch_no,
                                    qty: row.qty,
                                    warehouse: row.warehouse,
                                    item_code: row.item_code,
                                    sales_order: row.sales_order,
                                    customer: row.customer,
                                });
                            });

                            dialog.fields_dict.batch_selection.grid.refresh(); // Refresh table

                            // Show the dialog
                            dialog.show();
                        } else {
                            frappe.msgprint(__('No reserved batches available.'));
                        }
                    },
                });
            },
            __('Get Items From')
        );
    },
});
