for i in doc.items:
    if i.has_bom == 1 and i.bom_no == None:
        frappe.throw(f"Validate item {i.item_code} BOM before validating the current one")

if doc.work_order and doc.stock_entry_type == "Manufacture":
    work_order = frappe.get_doc("Work Order", doc.work_order)

    if bool(work_order.custom_use_detail_addtional_cost):
        # Initialize variables
        cost_dict = {}
        total_cost_per_hour = 0
        total_costs = doc.total_additional_costs
        operations = work_order.get("operations", [])

        for operation in operations:
            ws = frappe.get_doc("Workstation", operation.workstation)
            
            for line in ws.get("custom_details", []):
                cost_dict[line.account] = cost_dict.get(line.account, 0) + line.cost
                total_cost_per_hour = total_cost_per_hour + line.cost

                    
        # Clear `additional_costs` to avoid duplication
        doc.additional_costs = []
        for account, cost in cost_dict.items():
            doc.append("additional_costs", {
                "expense_account": account,
                "account_currency": "USD",
                "exchange_rate": 1,
                "description": "Operating Cost as per Work Order / BOM",
                "amount": cost * total_costs / total_cost_per_hour,
                "base_amount": cost * total_costs / total_cost_per_hour,
            })

